	ll q[200020];
	int f, r;
	void push(ll x){
		while(f-r > 0 && q[f-1] > x)--f;
		q[f++] = x;
	}
	void pop(ll x){if(x==q[r])++r;}
	ll read(){return f==r ? 1e18 : q[r];}
}Q;

