const int maxn = 1e5 + 500;
const ll inf = 2e18, mod = 1e9 + 7;

ll n, x[maxn], dp[maxn], seg[4 * maxn], E, T, ind;

void update(ll u, ll l, ll r, ll x, ll val)
{
	if (r - l == 1)
	{
		seg[u] += val;
		return;
	}
	ll mid = (l + r) / 2;
	if (x < mid)
		update(lchild(u), l, mid, x, val);
	else
		update(rchild(u), mid, r, x, val);
	seg[u] = min(seg[lchild(u)], seg[rchild(u)]);
}

ll query(ll u, ll l, ll r, ll b, ll e)
{
	if (e <= b)
		return inf;
	if (b <= l && r <= e)
		return seg[u];
	ll mid = (l + r) / 2, tmp = inf;
	if (b < mid)
		tmp = min(tmp, query(lchild(u), l, mid, b, e));
	if (e > mid)
		tmp = min(tmp, query(rchild(u), mid, r, b, e));
	return tmp;
}

