		if (2 * (x[i] - x[ind]) < T)
			ind--;
		dp[i] = min(2 * x[i] + query(0, 0, n + 1, 0, ind), T + dp[ind]);
		update(0, 0, n + 1, i, dp[i] - 2 * x[i + 1]);
	}
	cout << dp[n] + E << endl;
}

