#define int long long
const long long inf = 1ll * 1000 * 1000 * 1000 * 1000 * 1000;
const int N = 1e5 + 10; 
int a[N], nxt[N], n, e, t;
long long val;
long long seg[N << 2], lazy[N << 2], dp[N];
vector<int> fnd[N];
void relax(int id) {
	int lc = id * 2, rc = lc + 1;
	seg[lc] += lazy[id], lazy[lc] += lazy[id];
	seg[rc] += lazy[id], lazy[rc] += lazy[id];
	lazy[id];
	return;
}
void add(int l, int r, int id = 1, int s = 0, int e = n) {
	if (l == r)
		return;
	if (l <= s && e <= r) {
		seg[id] += val;
		lazy[id] += val;
		return;
	}
	if (e <= l || r <= s)
		return;
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	relax(id);
	add(l, r, lc, s, mid);
	add(l, r, rc, mid, e);
	seg[id] = min(seg[lc], seg[rc]);
}
void upd(int p, int id = 1, int s = 0, int e = n) {
	if (e - s == 1) {
		seg[id] = val;
		lazy[id] = 0;
		return;
	}
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	if (p < mid)
		upd(p, lc, s, mid);
	else
		upd(p, rc, mid, e);
	seg[id] = min(seg[lc], seg[rc]);
	return;
}
