void up(int u,int l,int r,int x,int y)
{
	if(r-l==1)
	{
		bt[u]=y;
		return ;
	}
	int md=(l+r)>>1;
	if(x<md)up(2*u,l,md,x,y);
	else up(2*u+1,md,r,x,y);
	bt[u]=min(bt[2*u],bt[2*u+1]);
}
int query(int u,int l,int r,int f,int s)
{
	if(l>=s||f>=r)return 1e18;
	if(f<=l&&r<=s)
	{
		return bt[u];
	}
	int md=(l+r)>>1;
	return min(query(2*u,l,md,f,s),query(2*u+1,md,r,f,s));
}
