        ll nx=x[i+1]-x[i];
        rep(j,i+1){
            dp[i+1][j]=min(dp[i+1][j],dp[i][j]+nx);
            if (i==j) continue;
            if (2*(x[i]-x[j+1])>=t){
                dp[i][i]=min(dp[i][i],dp[i][j]+2*(x[i]-x[j+1]));
            }
            else{
                dp[i][i]=min(dp[i][i],dp[i][j]+t);
            }
                /*
                if (2*(x[i+1]-x[j])>=t){
                    dp[i+1][j]=min(dp[i+1][j],dp[i+1][j-1]+x[i+1]-x[j]);
                }
                else{
                    dp[i+1][j]=min(dp[i+1][j],dp[i+1][j-1]+t-(x[i+1]-x[j]));
                }
                */
                /*
                if (2*(x[i]-x[j])>=t) {
                    dp[i+1][j]=min(dp[i+1][j],dp[i][j-1]+nx+2*(x[i]-x[j]));
                }
                else {
                    dp[i+1][j]=min(dp[i+1][j],dp[i][j-1]+nx+t);
                }
                */
        }
    }
    //rep(i,n+1)rep(j,n+1) cout << dp[i][j] << endl;
    //cout << dp[2][2] << endl;
    cout << dp[n][n]+e-x[n] << endl;
    return 0;
}

