		while (l < r){
			ll mid = (l + r) / 2;
			if ((a[i] - a[mid]) * 2 <= t) r = mid;
			else l = mid + 1;
		}
		lw = l;
		dp[i] = min(query(0, 0, n - 1, 0, lw - 1, 0) + 3 * a[i], query(0, 0, n - 1, lw, i - 1, 1) + a[i] + t);
