#include<stdio.h>
#include<stdlib.h>
#include<stdint.h>
#include<inttypes.h>

typedef int32_t i32;
typedef int64_t i64;

#define MAX(a,b) ((a) > (b) ? (a) : (b))
#define MIN(a,b) ((a) < (b) ? (a) : (b))

void run (void) {
  i32 n, e, t;
  scanf ("%" SCNi32 "%" SCNi32 "%" SCNi32, &n, &e, &t);
  if (n > 2000) exit (1);
  i32 *x = (i32 *) calloc (n + 2, sizeof (i32));
  for (i32 i = 1; i <= n; ++i) {
    scanf ("%" SCNi32, x + i);
  }
  x[n + 1] = e;
  i64 *dp = (i64 *) calloc (n + 1, sizeof (i64));
  for (i32 i = 1; i <= n; ++i) {
    i64 local = dp[i - 1] + x[i] - x[i - 1] + t;
    for (i32 j = i - 1; j > 0; --j) {
      i64 p = x[j] - x[j - 1];
      p += MAX(2 * (x[i] - x[j]), t);
      p += x[i] - x[j];
      local = MIN(local, p);
    }
    dp[i] = local;
  }
  printf("%" PRIi64 "\n", dp[n] + e - x[n]);
}

int main (void) {
  run();
  return 0;
}

#include<stdio.h>
