public class Main {
  private static class Task {
    class Q implements Comparable<Q> {
      int right;
      int left;
      long time;
      Q(int right, int left, long time) {
        this.right = right;
        this.left = left;
        this.time = time;
      }

      @Override
      public int compareTo(Q o) {
        if (this.time - o.time > 0) return 1;
        if (this.time - o.time < 0) return -1;
        return 0;
      }
    }

    void solve(FastScanner in, PrintWriter out) throws Exception {
      int N = in.nextInt();
      long E = in.nextInt();
      long T = in.nextInt();

      int[] x = in.nextIntArray(N);
      PriorityQueue<Q> queue = new PriorityQueue<>();
      queue.add(new Q(0, 0, x[0]));
      while (!queue.isEmpty()) {
        Q q = queue.poll();
        if (q.right == N) {
          out.println(q.time);
          return;
        }

        if (q.right < N - 1) {
          queue.add(new Q(q.right + 1, q.left, q.time + (x[q.right + 1] - x[q.right])));
        }

        //back
        long d = x[q.right] - x[q.left];
        long remain = Math.max(0, T - d * 2);
        long cur = q.time + d + remain + d;
        if (q.right < N - 1) {
          queue.add(new Q(q.right + 1, q.right + 1, cur + x[q.right + 1] - x[q.right]));
        } else {
          queue.add(new Q(q.right + 1, q.right + 1, cur + E - x[q.right]));
        }
      }

    }
  }

  /**
   * ã“ã“ã‹ã‚‰ä¸‹ã¯ãƒ†ãƒ³ãƒ—ãƒ¬ãƒ¼ãƒˆã§ã™ã€‚
   */
  public static void main(String[] args) throws Exception {
    OutputStream outputStream = System.out;
    FastScanner in = new FastScanner();
    PrintWriter out = new PrintWriter(outputStream);
    Task solver = new Task();
    solver.solve(in, out);
    out.close();
  }
  private static class FastScanner {
    private final InputStream in = System.in;
    private final byte[] buffer = new byte[1024];
    private int ptr = 0;
    private int bufferLength = 0;

    private boolean hasNextByte() {
      if (ptr < bufferLength) {
        return true;
      } else {
        ptr = 0;
        try {
          bufferLength = in.read(buffer);
        } catch (IOException e) {
          e.printStackTrace();
        }
        if (bufferLength <= 0) {
          return false;
        }
      }
      return true;
    }

    private int readByte() {
      if (hasNextByte()) return buffer[ptr++];
      else return -1;
    }

    private static boolean isPrintableChar(int c) {
      return 33 <= c && c <= 126;
    }

    private void skipUnprintable() {
      while (hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;
    }

    boolean hasNext() {
      skipUnprintable();
      return hasNextByte();
    }

    public String next() {
      if (!hasNext()) throw new NoSuchElementException();
      StringBuilder sb = new StringBuilder();
      int b = readByte();
      while (isPrintableChar(b)) {
        sb.appendCodePoint(b);
        b = readByte();
      }
      return sb.toString();
    }

    long nextLong() {
      if (!hasNext()) throw new NoSuchElementException();
      long n = 0;
      boolean minus = false;
      int b = readByte();
      if (b == '-') {
        minus = true;
        b = readByte();
      }
      if (b < '0' || '9' < b) {
        throw new NumberFormatException();
      }
      while (true) {
        if ('0' <= b && b <= '9') {
          n *= 10;
          n += b - '0';
        } else if (b == -1 || !isPrintableChar(b)) {
          return minus ? -n : n;
        } else {
          throw new NumberFormatException();
        }
        b = readByte();
      }
    }

    double nextDouble() {
      return Double.parseDouble(next());
    }

    double[] nextDoubleArray(int n) {
      double[] array = new double[n];
      for (int i = 0; i < n; i++) {
        array[i] = nextDouble();
      }
      return array;
    }

    double[][] nextDoubleMap(int n, int m) {
      double[][] map = new double[n][];
      for (int i = 0; i < n; i++) {
        map[i] = nextDoubleArray(m);
      }
      return map;
    }

    public int nextInt() {
      return (int) nextLong();
    }

    public int[] nextIntArray(int n) {
      int[] array = new int[n];
      for (int i = 0; i < n; i++) array[i] = nextInt();
      return array;
    }

    public long[] nextLongArray(int n) {
      long[] array = new long[n];
      for (int i = 0; i < n; i++) array[i] = nextLong();
      return array;
    }

    public String[] nextStringArray(int n) {
      String[] array = new String[n];
      for (int i = 0; i < n; i++) array[i] = next();
      return array;
    }

    public char[][] nextCharMap(int n) {
      char[][] array = new char[n][];
      for (int i = 0; i < n; i++) array[i] = next().toCharArray();
      return array;
    }

    public int[][] nextIntMap(int n, int m) {
      int[][] map = new int[n][];
      for (int i = 0; i < n; i++) {
        map[i] = nextIntArray(m);
      }
      return map;
    }
  }
}

import java.io.*;

public class Main {

	public static void main(String[] args) throws IOException {
		BufferedReader r = new BufferedReader(new InputStreamReader(System.in));
		String[] ld = r.readLine().split("\\s");
		int N = Integer.parseInt(ld[0]);
		long E = Long.parseLong(ld[1]);
		long T = Long.parseLong(ld[2]);
		String[] ps = r.readLine().split("\\s");
		long[] p = new long[N];
		for (int j = 0; j < N; j++)
			p[j] = Long.parseLong(ps[j]);
		int current = 0;
		long result = p[0];
		while (current < N) {
			int k = current + 1;
			while (k < N && p[k] - p[current] <= T)
				k++;
			result += 3 * (p[k - 1] - p[current]);
			if (2 * (p[k - 1] - p[current]) < T)
				result += T - 2 * (p[k - 1] - p[current]);
			if (k < N)
				result += p[k] - p[k - 1];
			current = k;
		}
		PrintWriter pw = new PrintWriter(System.out);
		pw.println(result + E - p[N - 1]);
		pw.close();
	}
}

