    Arrays.fill(dp, 1L << 50);
    dp[0] = 0;
    for (int i = 0; i < N; i++) {
      int cur = X[i];
      for (int j = i + 1; j <= N; j++) {
        int fp = X[i + 1];
        int lp = X[j];
        dp[j] = Math.min(dp[j], dp[i] + (lp - cur) + Math.max((lp - fp) * 2, T));
      }
