    static class Task {

        long E,T;
        int n;
        final int MAGIC = 2500;

        long dp[];
        long[] x;
        int[] next;
        int[] opt;

        public void solve(InputReader in, PrintWriter out) {

            n = in.nextInt();
            E = in.nextLong();
