    static class Task {

        long E,T;
        int n;
        final int MAGIC = 400;

        long dp[];
        long[] x;
        int[] next;

        public void solve(InputReader in, PrintWriter out) {

            n = in.nextInt();
            E = in.nextLong();
            T = in.nextLong();
            x = new long[n];
            next = new int[n];
            for (int i = 0; i < n; ++i) {
                x[i] = in.nextLong();
            }
            int pos = 0;
            for (int i = 0; i < n; ++i) {
                while ((pos < n) && (2*(x[pos] - x[i]) <= T)) {
                    pos++;
                }
                next[i] = pos;
            }
            dp = new long[n];
            dp[n - 1] = T + E - x[n - 1];
            for (int p = n - 2; p >= 0; --p) {
                long res = T + x[p + 1] - x[p] + dp[p + 1];
                int from = Math.max(p + 1, next[p] - MAGIC);
                int to = Math.min(n, next[p] + MAGIC);
                for (int i = from; i < to; ++i) {
                    if (i + 1 != n) {
                        res = Math.min(res, (x[i] - x[p]) + Math.max(T - x[i] + x[p], x[i] - x[p]) + x[i + 1] - x[p] + dp[i + 1]);
                    } else {
                        res = Math.min(res, (x[i] - x[p]) + Math.max(T - x[i] + x[p], x[i] - x[p]) + E - x[p]);
                    }
                }
                dp[p] = res;
            }
            out.println(dp[0] + x[0]);
