  static class TaskD {    
    int n, e, t;
    int[] x;

    void solve(int testNumber, InputReader in, PrintWriter out) {
      n = in.nextInt();
      e = in.nextInt();
      t = in.nextInt();
      x = new int[n + 1];
      for (int i = 1; i <= n; ++i)
        x[i] = in.nextInt();
      long[][] state = new long[3][n + 1];
      int ptr = 0;
      for (int i = 1; i <= n; ++i) {
        while (2 * (x[i] - x[ptr + 1]) > t)
          ++ptr;
        state[1][i] = ptr;
