  static class TaskD {    
    int n, e, t;
    int[] x;

    void solve(int testNumber, InputReader in, PrintWriter out) {
      n = in.nextInt();
      e = in.nextInt();
      t = in.nextInt();
      x = new int[n];
      for (int i = 0; i < n; ++i)
        x[i] = in.nextInt();
      if (n == 1) {
        out.println(e + t);
        return;
      }
      long ans = x[0];
      int idx = 0;
      while (idx < n - 1) { 
        long cnt = 2;
        int nxt = idx + 1;
        long temp = (x[nxt] - x[idx]) + cnt * t;
        while (nxt < n && (long) Math.max(t, 2 * (x[nxt] - x[idx])) + (x[nxt] - x[idx]) <= (x[nxt] - x[idx]) + cnt * t) {
          temp = (long) Math.max(t, 2 * (x[nxt] - x[idx])) + (x[nxt] - x[idx]);
          ++nxt;
          ++cnt;
        }
        ans += temp;
        if (nxt < n)
          ans += x[nxt] - x[nxt - 1];
        if (nxt == n - 1)
          ans += t;
        idx = nxt;
      }
      ans += e - x[n - 1];
      out.println(ans);
    }

  }

