			}

			SegRMQLong seg = new SegRMQLong(n+1);
			long[] ans = new long[n+1];
			for (int i = 0, l = 0; i < n; i++) {
				seg.add(0, i, 2 * d[i]);
				for (; (x[i] - x[l]) * 2 > T; l++) {
