import java.util.Arrays;
class SegmentTreeRMQL {
  public int M, H, N;
  public long[] st;

  public SegmentTreeRMQL(int n)
  {
      N = n;
      M = Integer.highestOneBit(Math.max(N-1, 1))<<2;
      H = M>>>1;
      st = new long[M];
      Arrays.fill(st, 0, M, Long.MAX_VALUE);
  }

  public SegmentTreeRMQL(long[] a)
  {
      N = a.length;
      M = Integer.highestOneBit(Math.max(N-1, 1))<<2;
      H = M>>>1;
      st = new long[M];
      for(int i = 0;i < N;i++){
          st[H+i] = a[i];
      }
      Arrays.fill(st, H+N, M, Long.MAX_VALUE);
      for(int i = H-1;i >= 1;i--)propagate(i);
  }

  public void update(int pos, long x)
  {
      st[H+pos] = x;
      for(int i = (H+pos)>>>1;i >= 1;i >>>= 1)propagate(i);
  }

  private void propagate(int i)
  {
      st[i] = Math.min(st[2*i], st[2*i+1]);
  }

  public long minx(int l, int r){
      long min = Long.MAX_VALUE;
      if(l >= r)return min;
      while(l != 0){
          int f = l&-l;
          if(l+f > r)break;
          long v = st[(H+l)/f];
          if(v < min)min = v;
          l += f;
      }

      while(l < r){
          int f = r&-r;
          long v = st[(H+r)/f-1];
          if(v < min)min = v;
          r -= f;
      }
      return min;
  }

  public long min(int l, int r){ return l >= r ? 0 : min(l, r, 0, H, 1);}

  private long min(int l, int r, int cl, int cr, int cur)
  {
      if(l <= cl && cr <= r){
          return st[cur];
      }else{
          int mid = cl+cr>>>1;
          long ret = Long.MAX_VALUE;
          if(cl < r && l < mid){
              ret = Math.min(ret, min(l, r, cl, mid, 2*cur));
          }
          if(mid < r && l < cr){
              ret = Math.min(ret, min(l, r, mid, cr, 2*cur+1));
          }
          return ret;
      }
  }

  public int firstle(int l, long v) {
      int cur = H+l;
      while(true){
          if(st[cur] <= v){
              if(cur < H){
                  cur = 2*cur;
              }else{
                  return cur-H;
              }
          }else{
              cur++;
              if((cur&cur-1) == 0)return -1;
              if((cur&1)==0)cur>>>=1;
          }
      }
  }

  public int lastle(int l, long v) {
      int cur = H+l;
      while(true){
          if(st[cur] <= v){
              if(cur < H){
                  cur = 2*cur+1;
              }else{
                  return cur-H;
              }
          }else{
              if((cur&cur-1) == 0)return -1;
              cur--;
              if((cur&1)==1)cur>>>=1;
          }
      }
  }
}


public class Main {

  private static void solve() {
    int n = ni();
    long e = ni();
    long t = ni();
    long[] x = nal(n);

    long[] dp = new long[n + 1];
    Arrays.fill(dp, Long.MAX_VALUE / 2);
    dp[0] = 0;
    SegmentTreeRMQL st = new SegmentTreeRMQL(n + 1);
    st.update(0, dp[0] - 2L * x[0]);

    int pre = 0;
    for (int i = 0; i < n; i ++) {
      while (pre < i && 2 * (x[i] - x[pre]) > t) {
        pre ++;
      }

      dp[i + 1] = Math.min(dp[i + 1], dp[pre] + Math.max(t,  2 * (x[i] - x[pre])));
      if (pre > 0) {
        dp[i + 1] = Math.min(dp[i + 1], st.min(0, pre) + 2 * x[i]);
      }
      if (i + 1 < n) {
        st.update(i + 1, dp[i + 1] - 2 * x[i + 1]);
      }
    }

    System.out.println(dp[n] + e);

  }



