public class Main {
  private static class Task {
    class Q implements Comparable<Q> {
      int right;
      int left;
      long time;
      Q(int right, int left, long time) {
        this.right = right;
        this.left = left;
        this.time = time;
      }

      @Override
      public int compareTo(Q o) {
        if (this.time - o.time > 0) return 1;
        if (this.time - o.time < 0) return -1;
        return 0;
      }
    }

    void solve(FastScanner in, PrintWriter out) throws Exception {
      int N = in.nextInt();
      long E = in.nextInt();
      long T = in.nextInt();

      int[] x = in.nextIntArray(N);

      PriorityQueue<Q> queue = new PriorityQueue<>();
      queue.add(new Q(0, 0, x[0]));
      while (!queue.isEmpty()) {
        Q q = queue.poll();
        int cur = q.right;
        int grow = q.left;
        long time = q.time;
        if (cur == N) {
          out.println(time);
          return;
        }

        long d = x[cur] - x[grow];
        long remain = Math.max(T - 2 * d, 0);
        if (cur == N - 1) {
          long t = time + d + remain + d + (E - x[cur]);
          queue.add(new Q(cur + 1, cur + 1, t));
          continue;
        }

        long e = x[cur + 1] - x[cur];
        long next = x[cur + 1] - x[grow];
        long nextRemain = T - 2 * next;
        if (nextRemain >= 0) {
          time += x[cur + 1] - x[cur];
          cur++;
          queue.add(new Q(cur, grow, time));
          continue;
        }
        queue.add(new Q(cur + 1, grow, time + e));
        long t = time + d + remain + d + e;
        queue.add(new Q(cur + 1, cur + 1, t));
      }
    }
  }

  /**
   * ã“ã“ã‹ã‚‰ä¸‹ã¯ãƒ†ãƒ³ãƒ—ãƒ¬ãƒ¼ãƒˆã§ã™ã€‚
   */
  public static void main(String[] args) throws Exception {
    OutputStream outputStream = System.out;
    FastScanner in = new FastScanner();
    PrintWriter out = new PrintWriter(outputStream);
    Task solver = new Task();
    solver.solve(in, out);
    out.close();
  }
  private static class FastScanner {
    private final InputStream in = System.in;
    private final byte[] buffer = new byte[1024];
    private int ptr = 0;
    private int bufferLength = 0;

    private boolean hasNextByte() {
      if (ptr < bufferLength) {
        return true;
      } else {
        ptr = 0;
        try {
          bufferLength = in.read(buffer);
        } catch (IOException e) {
          e.printStackTrace();
        }
        if (bufferLength <= 0) {
          return false;
        }
      }
      return true;
    }

    private int readByte() {
      if (hasNextByte()) return buffer[ptr++];
      else return -1;
    }

    private static boolean isPrintableChar(int c) {
      return 33 <= c && c <= 126;
    }

    private void skipUnprintable() {
      while (hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;
    }

    boolean hasNext() {
      skipUnprintable();
      return hasNextByte();
    }

    public String next() {
      if (!hasNext()) throw new NoSuchElementException();
      StringBuilder sb = new StringBuilder();
      int b = readByte();
      while (isPrintableChar(b)) {
        sb.appendCodePoint(b);
        b = readByte();
      }
      return sb.toString();
    }

    long nextLong() {
      if (!hasNext()) throw new NoSuchElementException();
      long n = 0;
      boolean minus = false;
      int b = readByte();
      if (b == '-') {
        minus = true;
        b = readByte();
      }
      if (b < '0' || '9' < b) {
        throw new NumberFormatException();
      }
      while (true) {
        if ('0' <= b && b <= '9') {
          n *= 10;
          n += b - '0';
        } else if (b == -1 || !isPrintableChar(b)) {
          return minus ? -n : n;
        } else {
          throw new NumberFormatException();
        }
        b = readByte();
      }
    }

    double nextDouble() {
      return Double.parseDouble(next());
    }

    double[] nextDoubleArray(int n) {
      double[] array = new double[n];
      for (int i = 0; i < n; i++) {
        array[i] = nextDouble();
      }
      return array;
    }

    double[][] nextDoubleMap(int n, int m) {
      double[][] map = new double[n][];
      for (int i = 0; i < n; i++) {
        map[i] = nextDoubleArray(m);
      }
      return map;
    }

    public int nextInt() {
      return (int) nextLong();
    }

    public int[] nextIntArray(int n) {
      int[] array = new int[n];
      for (int i = 0; i < n; i++) array[i] = nextInt();
      return array;
    }

    public long[] nextLongArray(int n) {
      long[] array = new long[n];
      for (int i = 0; i < n; i++) array[i] = nextLong();
      return array;
    }

    public String[] nextStringArray(int n) {
      String[] array = new String[n];
      for (int i = 0; i < n; i++) array[i] = next();
      return array;
    }

    public char[][] nextCharMap(int n) {
      char[][] array = new char[n][];
      for (int i = 0; i < n; i++) array[i] = next().toCharArray();
      return array;
    }

    public int[][] nextIntMap(int n, int m) {
      int[][] map = new int[n][];
      for (int i = 0; i < n; i++) {
        map[i] = nextIntArray(m);
      }
      return map;
    }
  }
}

