  static class TaskD {    
    int n, e, t;
    int[] x;

    void solve(int testNumber, InputReader in, PrintWriter out) {
      n = in.nextInt();
      e = in.nextInt();
      t = in.nextInt();
      x = new int[n];
      for (int i = 0; i < n; ++i)
        x[i] = in.nextInt();
      long[][] state = new long[3][n + 1];
      for (int i = 1; i <= n; ++i) {
        long a = 2 * x[i - 1] + state[2][i - 1];
        if (a < t)
          a = t;
        state[0][i] = Math.min(state[1][i - 1] + a, state[0][i - 1] + t);
        long b = state[0][i - 1] - 2 * x[i - 1];
        if (b < state[1][i - 1]) {
          state[1][i] = state[0][i - 1];
          state[2][i] = - 2 * x[i - 1];
        } else {
          state[1][i] = state[1][i - 1]; 
          state[2][i] = state[2][i - 1]; 
        }
      }
      state[0][n] += e;
      out.println(state[0][n]);
    }

  }

