        {
            sc.Dispose();
            pr.Dispose();
        }
        public int Size { get { return 1; } }
        public const long Mod = 1000000007;

        // ä½¿ç”¨ã™ã‚‹å¤‰æ•°ã‚’ã“ã“ã«æ›¸ã
        // string S;
        // int a;
        /// <summary>
        /// èª­ã¿è¾¼ã¿å‡¦ç†ã‚’ã“ã“ã«æ›¸ã
        /// </summary>
        void Read()
        {

        }
        /// <summary>
        /// ãƒ¡ã‚¤ãƒ³å‡¦ç†ã‚’ã“ã“ã«æ›¸ã
        /// </summary>
        void SolveOne()
        {
            long n = sc.nextLong();
            long e = sc.nextLong();
            long t = sc.nextLong();
            long[] x = sc.nextLong((int)n);

            long[] dp = new long[n + 1];   //dp[i+1]: ibanme ga owatta
            int[] bgnls = new int[n + 1];

            dp[0] = x[0];
            dp[1] = x[0] + t;
            for (int now = 1; now < n; now++)
            {
                dp[now + 1] = dp[now] + (x[now] - x[now - 1]) + t;
                int bgn = bgnls[now - 1];
                long katamiti = x[now] - x[bgn];
                long tmp = Math.Max(t + katamiti, katamiti * 3);
                if (dp[now + 1] > x[bgn] + tmp)
                {
                    dp[now + 1] = x[bgn] + tmp;
                }
                else
                {
                    bgnls[now] = now;
                }
            }

            Console.WriteLine(dp[n] + e - x[n - 1]);
        }
    }
