    {
        if (x.val == 0) return x;
        if (y == 0) return 1;
        var t = x ^ (y / 2);
        if ((y & 1) == 0) return t * t;
        return t * t * x;
    }

    public static ModInt operator-(ModInt x) => -(x.val);
    // inv(x) : x * inv(x) == 1
    // must : gcd(x, Mod) == 1
    public static ModInt operator~(ModInt x) => x ^ (Mod - 2);

