namespace AGC007
{
    public class D
    {
        private int N;
        private long E;
        private long T;
        private long[] xs;

        private long F(int current, int target)
        {
            long move = xs[target] - xs[current];
            long t = 2 * move; // â†’â†ã®é †ã«é€²ã¿ã€é€²ã¿éš›ã«ã‚­ãƒ£ãƒ³ãƒ‡ã‚£ã‚’ä¸Šã’ãŸçŠ¶æ…‹
            if (t < T) { t = T; }   // æœ€åˆã®ã‚¯ãƒžãŒã‚³ã‚¤ãƒ³ã‚’åãå‡ºã™ã¾ã§å¾…ã¤
            return t + move;   // ç§»å‹•åˆ†
        }

        private void Run()
        {
            var input = Console.ReadLine().Trim().Split();
            N = int.Parse(input[0]);
            E = long.Parse(input[1]);
            T = long.Parse(input[2]);
            xs = Console.ReadLine().Trim().Split().Select(long.Parse).ToArray();

            if (N > 2000) { throw new Exception("worth 600 points algorithm."); }

            long res = xs[0];   // é–‹å§‹åœ°ç‚¹ã‹ã‚‰æœ€åˆã®ã‚¯ãƒžã¾ã§ç§»å‹•
            int i = 0;
            while (i < N)
            {
                int go = i;
                while (true)
                {
                    int next = go + 1;
                    if (next >= N) { break; }
                    long temp1 = (xs[next] - xs[i] + (next - i + 1) * T);   // ã‚­ãƒ£ãƒ³ãƒ‡ã‚£ã‚’ä¸Žãˆã¦æ¯Žå›žå¾…ã¤
                    long temp2 = F(i, next);    // å…ˆã¾ã§ã‚­ãƒ£ãƒ³ãƒ‡ã‚£ã‚’ã‚ã’ã¦ã€æˆ»ã£ã¦ã‚³ã‚¤ãƒ³ã‚’å–ã‚‹
                    if (temp1 < temp2) { break; }
                    go = next;
                }
                res += F(i, go);
                if (go + 1 < N)
                {
                    res += xs[go + 1] - xs[go];
                }
                i = go + 1;
            }
            res += E - xs.Last();   // æœ€å¾Œã®ã‚¯ãƒžã‹ã‚‰ã‚´ãƒ¼ãƒ«ã¸ç§»å‹•

            Console.WriteLine(res);
        }

        public static void Main()
        {
            var old = Console.Out;
            using (var writer = new System.IO.StreamWriter(Console.OpenStandardOutput()) { AutoFlush = false })
            {
                Console.SetOut(writer);
                new D().Run();
                Console.Out.Flush();
                Console.SetOut(old);
            }
        }
    }
}

