int n;
struct Edge {
    int to;
    long dist;
}
Edge[][] g;
long md;

long[2][] dfs(int p) {
    if (g[p].empty) {
        return [[0L, 0L]];
    }
    Edge le = g[p][0], re = g[p][1];
    auto lt = dfs(le.to);
    auto rt = dfs(re.to);
    if (lt.length > rt.length) {
        swap(le, re);
        swap(lt, rt);
    }
    long ld = le.dist, rd = re.dist;
    long[2][] ans;
    foreach (co; lt) {
        //co[1] + ld + rd + rco[0] <= md
        int idx = binSearch!((i){return co[1]+ld+rd+rt[i][0] > md;})(-1L, rt.length).to!int-1;
        if (idx == -1) continue;
        ans ~= [co[0]+ld, rt[idx][1]+rd];
        ans ~= [rt[idx][1]+rd, co[0]+ld];
    }
    sort(ans);
    if (ans.empty) return ans;
    long ma = 10L^^15;
    int co = 0;
    foreach (e; ans) {
        if (ma <= e[1]) continue;
        ma = min(ma, e[1]);
        ans[co] = e;
        co++;
    }
    return ans[0..co];
}

