    alias NP = Node*;
    long mi;
    NP l, r;
    int sz;
    this(int sz) {
        this.sz = sz;
        if (sz == 1) return;
        l = new Node(sz/2);
        r = new Node(sz-sz/2);
    }
    void set(int k, long x) {
        if (sz == 1) {
            mi = x;
            return;
        }
        if (k < sz/2) {
            l.set(k, x);
        } else {
            r.set(k-sz/2, x);
        }
        mi = min(l.mi, r.mi);
    }
    long get(int a, int b) {
        if (b <= 0 || sz <= a) return 10L^^12;
        if (a <= 0 && sz <= b) return mi;
        return min(l.get(a, b), r.get(a-sz/2, b-sz/2));
    }
    string toString() {
        if (sz == 1) {
            return mi.to!string ~ ", ";
        }
        return l.toString ~ r.toString;
    }
}

