typedef long long ll;
const int INF=1e9+1;
ll n,t,e,st,pos[100005],dist; 
ll ans=0;
int main()
{
	scanf("%d%d%d",&n,&e,&t);
	for(int i=0;i<n;i++) scanf("%d",&pos[i]);
	ans+=pos[0]+e-pos[n-1];
	while(st<n-1)
	{
		//cout<<"*************"<<endl;
		//cout<<st<<"*****";
		int ed=st;
		dist=0;
		for(int i=st+1;i<n;i++)
		{
			dist+=pos[i]-pos[i-1];
			//cout<<dist<<" ";
			if(dist>t)
			{
				dist-=pos[i]-pos[i-1];
				ed=i-1;
				break;
			}
			else if(i==n-1) ed=1;
		}
		ans+=3*dist;
		if(2*dist<t) ans+=(ed-st+1)*(t-dist*2);
		if(ed<n-1) ans+=pos[ed+1]-pos[ed];
		if(ed==n-2) ans+=t;
		st=ed+1;
	}
	if(n==1) ans+=t;
	printf("%lld\n",ans);
	return 0;
}

#include <cstdio>
#include <algorithm>
using namespace std;

typedef long long ll;

const int maxn =  1e5 + 5;

int n, E, T, pos;
int a[maxn];
ll f[maxn], mn = 1e18;

int main()
{
    scanf("%d%d%d", &n, &E, &T);
    for(int i = 1; i <= n; i++)
        scanf("%d", &a[i]);
    for(int i = 1; i <= n; i++)
    {
        while(T < 2 * (a[i] - a[pos + 1]))
        {
            mn = min(mn, f[pos] - 2 * a[pos + 1]);
            pos++;
        }
        f[i] = min(f[i - 1] + T, mn + 2 * a[i]);
        if(pos < i - 1)
            f[i] = min(f[pos + 1] + T, f[i]);
    }
    printf("%lld\n", f[n] + E);
    return 0;
}

