int n,a[200010];

bool C(int x){
	int d[2] = {INF,INF};
	for(int i = n - 1;i < n * 2 - 2;i++){
		if(i == n * 2 - 2 || (a[i] >= x) == (a[i + 1] >= x)){
			d[a[i] >= x] = i - n + 1;
			break;
		}
	}
	for(int i = n - 1;i >= 0;i--){
		if(!i || (a[i] >= x) == (a[i - 1] >= x)){
			d[a[i] >= x] = n - 1 - i;
			break;
		}
	}
	return d[0] > d[1];
}

