int go(int x) {
	for (int i=1;i<=2*n-1;i++) b[i] = a[i] >= x;
	int p = n % 2 == 0 , cur = INF;
	int ret = b[n] ^ p;
	for (int i=n-1;i>=1;i--) {
		if (b[i] == b[i+1] && n-i < cur) {
			cur = n-i;
			ret = b[i];
		} 
	}
	for (int i=n+1;i<=2*n-1;i++) {
		if (b[i] == b[i-1] && i-n < cur) {
			cur = i-n;
			ret = b[i];
		}
	}
	return ret;
}

