namespace YuanTi
{
	const int N=100100;
	int s[N],t[N];
	int n,m;
	void initialize()
	{
		scanf("%d",&n),m=n*2-1;
		for(int i=1;i<=m;i++)
			scanf("%d",s+i);
	}
	bool check(int x)
	{
		for(int i=1;i<=m;i++)t[i]=(s[i]<=x);

		int a=-1,b=-1;
		for(int i=n;i>1;i--)
			if(t[i]==t[i-1]){a=i;break;}
		for(int i=n;i<m;i++)
			if(t[i]==t[i+1]){b=i;break;}
		if(a<0 && b<0)return 1;
		if(a<0)return t[b];
		if(b<0)return t[a];
		if(n-a<b-n)return t[a];
		return t[b];
	}
	int getans()
	{
		int u=m,d=1,mid;
		while(u>d)
		{
			if(check(mid=(u+d)>>1))u=mid;
			else d=mid+1;
		}
		return d;
	}
	void solve()
	{
		initialize();

		printf("%d\n",getans());
	}
}
int main()
{
	YuanTi::solve();
	return 0;
}

