struct Fenwick {
	vi t;
	Fenwick(int n) : t(n+1) {}
	int sum(int p) {
		++p;
		int ret = 0;
		while(p > 0) {
			ret += t[p];
			p &= (p-1);
		}
		return ret;
	}
	void add(int p, int v) {
		++p;
		while(p < t.size()) {
			t[p] += v;
			p += (p & -p);
		}
	}
};

int n;

int findMean(Fenwick& s, int k) {
	int left = 1;
	int right = 2*n - 1;
	while(left < right) {
		int mid = (left + right)/2;
		if(s.sum(mid) >= k) {
			right = mid;
		} else {
			left = mid+1;
		}
	}
	return left;
}

