        bool ok = false;
        int i = l + 1;
        while(i <= r){
            if(a[i] == a[i - 1]){
                if(i <= n) l = i - 1; else r = i;
                ok = true;
            }
            i++;
        }
        if(!ok) zok++; else zok = 0;
        if(zok == 5) {cout << a[n] << endl; return 0;}
    }
