int comp(VI &a, int pos, int s){
    if((a[pos-1]<=s and a[pos]<= s) or (a[pos]<=s and a[pos+1]<=s)) return 1;
    if((a[pos-1]>s and a[pos]>s) or (a[pos]>s and a[pos+1]>s)) return -1;
    return 0;
}

bool check(int s, VI &a){
    int n=((int)a.size()+1)/2;
    for(int i=n-1; i<a.size(); i+=2){
        if(comp(a,i,s)==1) return true;
        if(comp(a,i,s)==-1) return false;
    }
    for(int i=n-1; i>0; i-=2){
        if(comp(a,i,s)==1) return true;
        if(comp(a,i,s)==-1) return false;
    }
    int res=0;
    if(a[0]<=s)++res;
    if(a[n-1]<=s)++res;
    if(a[a.size()-1]<=s)++res;
    return res>=2;
}

