bool TOP(vector<int> &w){
  if(w[n-2]+w[n-1]+w[n]==3)return 1;
  if(w[n-2]+w[n-1]+w[n]<=1)return 0;
  if(w[n-1])return 1;
  for(int i=0;i<n-1;i++){
    int l=w[n-2-i],r=w[n+i];
    if(i&1){if(l||r)return 1;}
    else if(!l||!r)return 0;
  }
  return !(n&1);
}

