bool chk(int x){
    vector<int> tmp;
    for (int i=0;i<2*n-1;++i) {
        int tp = 0;
        if (d[i] >= x) tp = 1;
        else tp = 0;
        tmp.eb(tp);
    }
    debug(x,tmp);
    if ((tmp[n-2] == 1 && tmp[n-1] == 0 && tmp[n] == 1) || (tmp[n-2] == 0 && tmp[n-1] == 1 && tmp[n] == 0)) {
        for (int i=2;i<=2*n;++i) {
            if (n-1-i < 0) {
                return tmp[0];
            }
            if (i % 2 == 0) {
                if (tmp[n-i-1] != tmp[n-1] || tmp[n+i-1] != tmp[n-1]) {
                    return !tmp[n-1];
                }
            }
            else {
                if (tmp[n-i-1] != (!tmp[n-1]) || tmp[n+i-1] != (!tmp[n-1])) {
                    return tmp[n-1];
                }
            }
        }
    }
    else {
        if (tmp[n-2] + tmp[n-1] + tmp[n] >= 2) return 1;
        return 0;
    }
    return 0;
}
