const int SIZE=5+2*1e5;
const LL INF=1<<30;
int x[SIZE];
int conv[SIZE];
int N;
int NG;
int solve(int f){
	REP(i,NG) conv[i]=x[i]-f;
	REP(i,NG) if(conv[i]!=0) conv[i]/=abs(conv[i]);
	int now=N-1;
	int s=0,e=NG-1;
	FOR(i,now,NG-1) 
		if(conv[i]==conv[i+1]) {e=i;break;} 
	for(int i=now;i>0;i--)
		if(conv[i]==conv[i-1]) {s=i;break;}
	int ns=conv[s],ne=conv[e];
	for(int i=s+2;i<NG;i+=2)
		if(conv[i]==conv[i-2]) s++;
		else break;
	for(int i=e-2;i>0;i-=2)
		if(conv[i]==conv[i+2]) e--;
		else break;

	if(now<=s) return ns;
	else if(e<=now) return ne;
	return 0;
}
