int n,a[200005];
bool check(int val){
	int i,prevR=-1,prevV=-1;
	//printf("check %")
	for(i=0;i<n*2-1;++i){
		if((a[i]>=val)!=(a[i+1]>=val) || i==n*2-2){

			if(!i || (a[i]>=val)!=(a[i-1]>=val)) continue;
			int L,R=i,nL,cur=(a[i]>=val);
			for(L=i;L && (a[L-1]>=val)==cur;--L);
			nL=L-n+1;

			if(prevR+n-1<nL){
				if(n-1<=prevR+n-1) return prevV;
				if(n-1>prevR+n-1 && n-1<nL) return (cur+nL-n+1)%2;
				if(n-1>=nL && n-1<=R) return cur;
			}
			else if(prevV==cur){
				if(n-1<=R) return cur;
			}
			else{
				int M=(prevR+L)/2;
				if(n-1<=M) return prevV;
				if(n-1>M && n-1<=R) return cur;
			}
			prevR=R;
			prevV=cur;
		}
	}
	return a[0]>=val;
}
