int n,a[MN+5],b[MN+5];
int Solve(int x)
{
	for(int i=1;i<n<<1;++i) b[i]=a[i]>=x;
	if(b[n]==b[n-1]||b[n]==b[n+1]) return b[n];
	int l=0,r=n*2;
	for(int i=n-2;i;--i) if(b[i]==b[i+1]) {l=i;break;} 
	for(int i=n+2;i<n<<1;++i) if(b[i]==b[i-1]){r=i;break;}
	if(l==0&&r==n*2) return b[n]^(~n&1);
	else return (n-l<=r-n)?b[l]:b[r]; 
}
