bool check(vector<int> &d, ll md, int n){
	for(int i = 0; i <n-1; i++){
		for(int j = n-i-1; j <= n+i; j += 2*i+1){
			if(d[j]<md && d[j+1]<md) return false;
			if(d[j]>=md && d[j+1]>=md) return true;
		}
	}
	return d[0]>=md;
}

