bool calc(ll mid){
    vector<ll> v;
    //p(mid);
    for(ll i=0;i<n;i++){
        v.pb(x[i] >= mid);
    }
    ll l, r;
    if(v[(n-1)/2] == 1){
        l = (n-1)/2, r = (n-1)/2;
    }else{
        l = (n-1)/2 + 1, r = (n-1)/2 - 1;
    }
    while(true){
        if(v[l-1] == 0 && v[l-2] == 1 && l-2 >= 0){
            l -= 2;
        }else{
            break;
        }
    }
    while(true){
        if(v[r+1] == 0 && v[r+2] == 1 && r+2 < n){
            r += 2;
        }else{
            break;
        }
    }
    //pe(l);p(r);
    if(!(l <= (n-1)/2 && (n-1)/2 <= r)){
        return (v[(n-1)/2] == 1);
    }
    if((l + r) / 2 == (n-1)/2){
        return (v[r+1] == 1) || (v[l-1] == 1);
    }else{
        ll center = (l + r) / 2;
        if(center < (n-1)/2){
            return (v[r+1] == 1);
        }else{
            return (v[l-1] == 1);
        }
    }
}

