const int N = 100000 + 5;

int n;
int a[N << 1], p[N << 1];

int check(int x) {
	for (int i = 1; i <= 2 * n - 1; i++) a[i] = (p[i] >= x ? 1 : 0);
	int last = 1;
	for (int i = 1, j; i <= 2 * n - 1; i = j + 1) {
		j = i;
		while (j + 1 <= 2 * n - 1 && a[j + 1] == a[i]) j++;
		if (i != j) {
			if (last <= i - 1 && last <= n && n <= i - 1) {
				int len = i - 1 - last + 1;
				if (len & 1) return !a[last];
				else {
					int pos = last + len / 2 - 1;
					return n <= pos ? !a[last] : !a[i - 1];
				}
			}
			if (i <= n && n <= j) return a[i];
			last = j + 1;
		}
	}
	return a[1];
}

