	int l = 1, r = 2 * n - 1, ans;
	while (l <= r) {
		int m = (l + r) >> 1;
		if (check(m) == 1) {
			ans = m;
			l = m + 1;
		} else r = m - 1;
	}
	printf("%d\n", ans);
