bool f(vector<int>& a, int mid) {
  int N = a.size();
  vector<bool> v(N);
  REP(i, N) v[i] = a[i] >= mid;

  int m = N / 2;

  int l = m - 1;
  while (l >= 0 && v[l] != v[l + 1]) l--;

  int r = m + 1;
  while (r < N && v[r] != v[r - 1]) r++;

  if (l == -1 && r == N) {
    int nsteps = (N - 1) / 2;
    return v[m] ^ (nsteps % 2);
  }

  return m - l < r - m ? v[l] : v[r];
}

