int len[N],op[N];
bool Check(int x)
{
	for (int i=1;i<=n;++i) if (A[i]>=x) now[i]=1;else now[i]=0;
	int top=0,cnt=0;
	for (int i=1;i<=n;)
	{
		int pos=i;
		while (now[pos]==now[i] && i<=n) ++i;
		if (i-pos>1)
		{
			if (top && now[pos]==op[top]) len[top]+=i-pos+cnt;
			else 
			{
				++top; len[top]=i-pos;
				if (top-1) len[top-1]+=cnt>>1,len[top]+=cnt>>1;else len[top]+=cnt;
				op[top]=now[pos];
			}
			cnt=0;
		}else cnt++;
	}
	len[top]+=cnt;cnt=0;
	int sum=0;
	for (int i=1;i<=top;++i) sum+=len[i];
	int l=1,r=top;
	while (l!=r)
	{
		int del=min(len[l],len[r]);
		len[l]-=del; len[r]-=del;
		if (!len[l]) ++l;
		if (!len[r]) --r;
	}
	return op[l];
