bool IsGood(const int n, const vector<int>& a) {
    int lfDist = -1;
    int lfValue;
    for (int i = n - 1; i > 0; --i) {
        if (a[i] == a[i - 1]) {
            lfValue = a[i];
            lfDist = (n - 1) - i;
            break;
        }
    }
    int rgDist = -1;
    int rgValue;
    for (int i = n - 1; i + 1 < (int) a.size(); ++i) {
        if (a[i] == a[i + 1]) {
            rgValue = a[i];
            rgDist = i - (n - 1);
            break;
        }
    }

    if (lfDist == -1 && rgDist == -1) {
        if (n & 1) {
            return a[n - 1];
        } else {
            return 1 - a[n - 1];
        }
    } else if (lfDist != -1 && rgDist != -1) {
        if (lfDist <= rgDist) {
            return lfValue;
        } else {
            return rgValue;
        }
    } else if (lfDist != -1) {
        return lfValue;
    } else if (rgDist != -1) {
        return rgValue;
    }
    assert(false);
}

