            l = i - (n - 1);
            break;
        }
    }
    for (int i = n - 1; i > 0; i--) {
        if(c[i] == c[i - 1]) {
            l = min(l, (n - 1) - i);
            break;
        }
    }
    return (res + l) % 2;
}
