int const N = 1e5 + 41;

int n;
vector<int> p, v;

pii zigzag(vector<int> v){
	pii ret = mp(1, 0);
	rep(i, 1, sz(v)) if(v[i] == v[i-1]) ret.first = false;
	if(!ret.first) return ret;
	int change = ((n / 2) & 1);
	ret.second = (v[n/2] ^ change);
	return ret;
}

int check(int val){
	v.clear();
	rep(i, 0, n){
		int value;
		if(p[i] >= val) value = 1;
		else value = 0;
		v.pb(value);
	}
	pii ret = zigzag(v);
	if(ret.first) return ret.second;

	int lp = -1;
	int rp = -1;
	per(i, n/2 + 1, 1) if(v[i] == v[i-1]){
		lp = i;
		break;
	}
	rep(i, n/2, n-1) if(v[i] == v[i+1]){
		rp = i;
		break;
	}
	if(lp == -1 || rp == -1){
		if(lp == -1) return v[rp];
		else return v[lp];
	}else{
		if(v[lp] == v[rp]) return v[lp];
		rep(i, lp+1, rp-1) if(v[i] == v[lp]) ++lp;
		else --rp;
	}
	if(lp >= n/2) return v[lp];
	else return v[rp];
}

