using namespace std;

int n, a[200009]; bool u[200009], v[200009], w[200009];

bool solve(int pos) {
	for (int i = 1; i <= n; i++) { v[i] = false; w[i] = false; }
	for (int i = 1; i <= n; i++) {
		if (a[i] <= pos) u[i] = true; else u[i] = false;
	}
	int s = 1000000007;
	for (int i = 2; i <= n; i++) {
		if (u[i] == true && u[i - 1] == true) {
			for (int j = s; j <= i; j++) v[j] = true;
			s = i - 1; v[i] = true; v[i - 1] = true;
		}
		if (u[i] == false && u[i - 1] == false) {
			w[i] = true; w[i - 1] = true;
			s = 1000000007;
		}
	}
	if (u[1] == false) w[1] = true;
	if (u[n] == false) w[n] = true;
	int target = n / 2 + 1;
	if (v[target] == true) return true;
	if (w[target] == true) return false;

	int L = -1; vector<pair<int, int>>G;
	for (int i = 1; i <= n; i++) {
		if (v[i] == true) {
			if (L == -1) continue;
			if (L <= target && target <= i) {
				if ((L + i) / 2 <= target) return true;
			}
			L = -1;
		}
		else if (w[i] == false) {
			if (L == -1) L = i;
		}
		else {
			if (L != -1) {
				G.push_back(make_pair(L, i - 1));
				L = -1;
			}
		}
	}
	if (L != -1) G.push_back(make_pair(L, n));
	int R = -1;
	for (int i = n; i >= 1; i--) {
		if (v[i] == true) {
			if (R == -1) continue;
			if (R >= target && target >= i) {
				if ((R + i) / 2 >= target) return true;
			}
			R = -1;
		}
		else if (w[i] == false) {
			if (R == -1) R = i;
		}
		else { R = -1; }
	}
	if (G.size() == 0) return false;
	if (G[0] == make_pair(1, n)) return true;
	return false;
}

int main() {
	cin >> n; n = n * 2 - 1;
	for (int i = 1; i <= n; i++) cin >> a[i];
	int LL = 1, RR = n + 1, MM, maxn = 1000000007;
	for (int j = 0; j < 20; j++) {
		MM = (LL + RR) / 2;
		bool a1 = solve(MM);
		if (a1 == true) { maxn = min(maxn, MM); RR = MM; }
		else { LL = MM; }
	}
	cout << maxn << endl;
	return 0;
}

