	if(b[n-2] != b[n-1] && b[n-1] != b[n]){
		int w = n-2, e = n;
		while(w > 0 && b[w-1] != b[w]) w--;
		while(e+1 < 2*n-1 && b[e+1] != b[e]) e++;
