{
    bool k[200020] = {0},f[200020] = {0},F = false;
    if(A[0] >= mid)k[0] = true;
    else k[0] = false;

    for(int i = 1; i < N * 2 - 1; i++)
    {
        if(A[i] >= mid)k[i] = true;
        else k[i] = false;
        if(k[i] == k[i - 1])
