            f[i] = f[i - 1] = true;
            F = true;
        }
    }

    if(!F)return k[N - 1] ^ (N % 2 == 0);

    for(int i = 0; i < N; i++)
    {
        if(f[N - 1 + i])return k[N - 1 + i];
        if(f[N - 1 - i])return k[N - 1 - i];
    }
    return false;
}

