int n;
const int N = 200000;
int ai[N];
int check(int t)
{
    int ok = 1;
    if (ai[n] >= t && (ai[n + 1] >= t || ai[n - 1] >= t)) return 1;
    for (int i = 1; i < n * 2; i += 2) if (ai[i] < t) ok = 0;
    if (ok) return 1;
    ok = 1;
    for (int i = 1; i < n * 2; i += 2)
        if (ai[i] < t) ok = 0;
        else if (ai[i + 1] >= t && i >= n && ok) return 1;
    ok = 1;
    for (int i = n * 2 - 1; i > 0; i -= 2)
        if (ai[i] < t) ok = 0;
        else if (ai[i - 1] >= t && i <= n && ok) return 1;
    ok = 1;
    for (int i = n; i < 2 * n; ++ i)
        if (ai[i] < t && ai[i + 1] < t || i == 2 * n - 1) ok = 0;
        else if (ai[i] >= t && ai[i + 1] >= t) break;
    for (int i = n; i; -- i)
        if (ai[i] < t && ai[i - 1] < t || i == 1) ok = 0;
        else if (ai[i] >= t && ai[i - 1] >= t) break;
    return ok;
}
int main()
{
    scanf("%d", &n);
    for (int i = 1; i < n * 2; ++ i) scanf("%d", &ai[i]);
    int l = 1, r = n * 2 - 1;
    while (l < r)
    {
        int m = (l + r + 1) / 2;
        if (check(m)) l = m; else r = m - 1;
    }
    printf("%d\n", r);
}

