using namespace std;

typedef long long llint;

const int MAXN = 200010;
const int INF = 1e9;

int n, a[MAXN], b[MAXN];

int check(int x) {
  REP(i, n) b[i] = (a[i] >= x) ? 1 : 0;

  int best = -1, bdist = INF;
  REP(i, n) {
    if ((i > 0 && b[i] == b[i-1]) || (i+1 < n && b[i] == b[i+1])) {
      int cdist = abs(n/2 - i);
      if (cdist < bdist) {
	best = i;
	bdist = cdist;
      }
    }
  }

  if (best == -1) {
    int ret = b[n/2];
    if (((n+1)/2) % 2 == 0) ret ^= 1;
    return ret;
  }

  return b[best];
}

int solve() {
  int lo = 1, hi = 2*n-1, ret = 1;
  while (lo <= hi) {
    int mid = (lo + hi) / 2;
    if (check(mid)) {
      ret = mid;
      lo = mid + 1;
    } else
      hi = mid - 1;
  }
  return ret;
}

int main(void) {
  scanf("%d",&n);
  n = 2*n - 1;
  REP(i, n) scanf("%d",&a[i]);
  printf("%d\n",solve());
  return 0;
}

